import groovy.xml.XmlUtil
def response = context.response
def xmlResponse = new XmlSlurper().parseText(response)
def ns2 = new groovy.xml.Namespace("http://www.soap.com/gen")
assert xmlResponse .declareNamespace(ns2: "http://www.soap.com/gen") .Body .'ns2:postSvConfResponse' .'ns2:status' .text() == '0', "Could not create, already exists, or wrong attributes."

import groovy.xml.XmlUtil

def response = context.response

def xmlResponse = new XmlSlurper().parseText(response)

def ns2 = new groovy.xml.Namespace("http://www.soap.com/gen")
def status = xmlResponse
    .declareNamespace(ns2: "http://www.soap.com/gen")
    .Body
    .'ns2:getSvConfResponse'
    .'ns2:status'
    .text()

if (status == '0') {
    assert xmlResponse.Body.'ns2:getSvConfResponse'.'ns2:svConf'.'ns2:attrName'.text() == 'Test' : "'attrName' not expected value."
    assert xmlResponse.Body.'ns2:getSvConfResponse'.'ns2:svConf'.'ns2:attrValue'.text() == 'Test1 value' : "'attrValue' not expected value.."
    assert xmlResponse.Body.'ns2:getSvConfResponse'.'ns2:svConf'.'ns2:attrDesc'.text() == 'Lorem Ipsum' : "'attrDesc' not expected value.."
} else {
    assert false : "Does not exist."
}

